void
ParticipantUpdateDataReaderImpl::dec_ref_data_element(
  ::OpenDDS::DCPS::ReceivedDataElement* item
  )
{
  using ::OpenDDS::DCPS::ReceivedDataElement;

  if (0 == item->dec_ref())
  {
    if (item->registered_data_ != 0)
    {
      ::OpenDDS::Federator::ParticipantUpdate* const ptr
        = static_cast< ::OpenDDS::Federator::ParticipantUpdate* >(item->registered_data_);
      ACE_DES_FREE (ptr,
                    data_allocator_->free,
                    ParticipantUpdate );
    }

    ACE_DES_FREE (item,
                  rd_allocator_->free,
                  ReceivedDataElement);
  }
}


