void
PublicationUpdateDataReaderImpl::release_loan (
    ::OpenDDS::Federator::PublicationUpdateSeq & received_data
  )
{
  received_data.length(0);
}


void
PublicationUpdateDataReaderImpl::dec_ref_data_element(
  ::OpenDDS::DCPS::ReceivedDataElement* item
  )
{
  using ::OpenDDS::DCPS::ReceivedDataElement;

  if (0 == item->dec_ref())
  {
    if (item->registered_data_ != 0)
    {
      ::OpenDDS::Federator::PublicationUpdate* const ptr
        = static_cast< ::OpenDDS::Federator::PublicationUpdate* >(item->registered_data_);
      ACE_DES_FREE (ptr,
                    data_allocator_->free,
                    PublicationUpdate );
    }

    ACE_DES_FREE (item,
                  rd_allocator_->free,
                  ReceivedDataElement);
  }
}


::DDS::ReturnCode_t
PublicationUpdateDataReaderImpl::return_loan (
    ::OpenDDS::Federator::PublicationUpdateSeq & received_data,
    ::DDS::SampleInfoSeq & info_seq)
{
  // Some incomplete tests to see that the data and info are from the
  // same read.
  if (received_data.length() != info_seq.length())
  {
    return ::DDS::RETCODE_PRECONDITION_NOT_MET;
  }

  if (received_data.release())
  {
    // nothing to do because this is not zero-copy data
    return ::DDS::RETCODE_OK;
  }
  else
  {
    info_seq.length(0);
    received_data.length(0);
  }
  return ::DDS::RETCODE_OK;
}

::DDS::ReturnCode_t
PublicationUpdateDataReaderImpl::get_key_value (
    ::OpenDDS::Federator::PublicationUpdate & key_holder,
    ::DDS::InstanceHandle_t handle)
{
  ACE_GUARD_RETURN (ACE_Recursive_Thread_Mutex,
                    guard,
                    this->sample_lock_,
                    ::DDS::RETCODE_ERROR);

  InstanceMap::iterator const the_end = instance_map_.end ();
  for (InstanceMap::iterator it = instance_map_.begin ();
       it != the_end;
       ++it)
  {
    if (it->second == handle)
    {
      key_holder = it->first;
      return ::DDS::RETCODE_OK;
    }
  }

  return ::DDS::RETCODE_ERROR;
}


::DDS::InstanceHandle_t
PublicationUpdateDataReaderImpl::lookup_instance (
    const ::OpenDDS::Federator::PublicationUpdate & instance_data)
{
  InstanceMap::const_iterator const it = instance_map_.find(instance_data);

  if (it == instance_map_.end())
  {
    return ::DDS::HANDLE_NIL;
  }
  else
  {
    return it->second;
  }
}

